import{_ as e,o as a,c as t,N as r}from"./chunks/framework.bc3f8955.js";const m=JSON.parse('{"title":"SRE","description":"","frontmatter":{},"headers":[],"relativePath":"ci/gitlab-jenkins.md"}'),i={name:"ci/gitlab-jenkins.md"},s=r('<h1 id="sre" tabindex="-1">SRE <a class="header-anchor" href="#sre" aria-label="Permalink to &quot;SRE&quot;">​</a></h1><p><a href="http://c.100credit.cn/pages/viewpage.action?pageId=93312883" target="_blank" rel="noreferrer">SRE内部培训分享-文档</a></p><p><a href="http://c.100credit.cn/pages/viewpage.action?pageId=60420542" target="_blank" rel="noreferrer">sre平台架构升级优化</a></p><h3 id="架构图" tabindex="-1">架构图 <a class="header-anchor" href="#架构图" aria-label="Permalink to &quot;架构图&quot;">​</a></h3><p><img src="https://cdn.jsdelivr.net/gh/Eadela/pic-go/img/202305171119297.png" alt="微服务架构"></p><p>各个应用层在微服务架构下的职责：</p><ul><li><strong>运行环境层</strong>：提供基础设施服务，包括服务器，IT安全配置以及容器云平台。未来所有的服务都会运行在容器云平台上</li><li><strong>数据存储层</strong>：提供数据存储能力，对于不同的数据类型，提供了不同的存储方式。Mysql用于应用数据的存储，redis用于缓存数据的存储，ES集群用于快速检索</li><li><strong>基础服务层</strong>：提供基础公共服务能力，除了为sre平台发布平台本身提供基础服务 日志服务(ELK)提供日志采集，存储，分析和展示服务； 消息服务提供组件间的通信能力； 任务调度和后台作业(Quaz)提供了长耗时任务队列的调度和处理服务； 身份认证服务面向开放提供了统一的身份认证能力。</li><li><strong>应用服务层</strong>：研发协同业务服务，主要包含基础服务，持续集成服务、质量服务和持续交付服务。</li><li><strong>API接口层</strong>：API网关为服务消费端提供了统一的服务入口。除了网关，还必须实现服务治理：服务的注册、发现、负载、容错、降级、日志。</li><li><strong>展示层</strong>：展示层是通过API网关来使用应用服务。</li></ul><h3 id="devops架构cicd程序设计图" tabindex="-1">DevOps架构CICD程序设计图: <a class="header-anchor" href="#devops架构cicd程序设计图" aria-label="Permalink to &quot;DevOps架构CICD程序设计图:&quot;">​</a></h3><p><img src="https://cdn.jsdelivr.net/gh/Eadela/pic-go/img/202305171119322.png" alt="CICD程序架构图"></p>',9),n=[s];function o(l,c,p,d,g,_){return a(),t("div",null,n)}const f=e(i,[["render",o]]);export{m as __pageData,f as default};
